script:
frappe.ui.form.on('Employee leave request2', {
    from_date(frm) {
        calculate_days(frm);
    },
    to_date(frm) {
        calculate_days(frm);
    }
});

function calculate_days(frm) {
    if (frm.doc.from_date && frm.doc.to_date) {
        if (frm.doc.from_date > frm.doc.to_date) {
            frappe.msgprint(__('From Date cannot be greater than To Date'));
            frm.set_value('total_days', 0);
            return;
        }

        let diff = frappe.datetime.get_day_diff(
            frm.doc.to_date,
            frm.doc.from_date
        ) + 1;

        frm.set_value('total_days', diff);
    }
}
